<?php class Security{private $data;function purify($data){global $db;$data=filter_var($data,FILTER_SANITIZE_STRING);$data=$db->real_escape_string($data);return $data;}function printData($data){return html_entity_decode($data,ENT_QUOTES,'UTF-8');}function checkUsername($data){if(ctype_alnum($data)){return true;}else{return false;}}function washWidget($data){global $db;return htmlspecialchars($data);}function strip($data){$data=str_replace(array('&amp;','&lt;','&gt;'),array('&amp;amp;','&amp;lt;','&amp;gt;'),$data);$data=preg_replace('/(&#*\w+)[\x00-\x20]+;/u','$1;',$data);$data=preg_replace('/(&#x*[0-9A-F]+);*/iu','$1;',$data);$data=html_entity_decode($data,ENT_COMPAT,'UTF-8');$data=preg_replace('#(<[^>]+?[\x00-\x20"\'])(?:on|xmlns)[^>]*+>#iu','$1>',$data);$data=preg_replace('#([a-z]*)[\x00-\x20]*=[\x00-\x20]*([`\'"]*)[\x00-\x20]*j[\x00-\x20]*a[\x00-\x20]*v[\x00-\x20]*a[\x00-\x20]*s[\x00-\x20]*c[\x00-\x20]*r[\x00-\x20]*i[\x00-\x20]*p[\x00-\x20]*t[\x00-\x20]*:#iu','$1=$2nojavascript...',$data);$data=preg_replace('#([a-z]*)[\x00-\x20]*=([\'"]*)[\x00-\x20]*v[\x00-\x20]*b[\x00-\x20]*s[\x00-\x20]*c[\x00-\x20]*r[\x00-\x20]*i[\x00-\x20]*p[\x00-\x20]*t[\x00-\x20]*:#iu','$1=$2novbscript...',$data);$data=preg_replace('#([a-z]*)[\x00-\x20]*=([\'"]*)[\x00-\x20]*-moz-binding[\x00-\x20]*:#u','$1=$2nomozbinding...',$data);$data=preg_replace('#(<[^>]+?)style[\x00-\x20]*=[\x00-\x20]*[`\'"]*.*?expression[\x00-\x20]*\([^>]*+>#i','$1>',$data);$data=preg_replace('#(<[^>]+?)style[\x00-\x20]*=[\x00-\x20]*[`\'"]*.*?behaviour[\x00-\x20]*\([^>]*+>#i','$1>',$data);$data=preg_replace('#(<[^>]+?)style[\x00-\x20]*=[\x00-\x20]*[`\'"]*.*?s[\x00-\x20]*c[\x00-\x20]*r[\x00-\x20]*i[\x00-\x20]*p[\x00-\x20]*t[\x00-\x20]*:*[^>]*+>#iu','$1>',$data);$data=preg_replace('#</*\w+:\w[^>]*+>#i','',$data);do{$old_data=$data;$data=preg_replace('#</*(?:applet|b(?:ase|gsound|link)|embed|frame(?:set)?|i(?:frame|layer)|l(?:ayer|ink)|meta|object|s(?:cript|tyle)|title|xml)[^>]*+>#i','',$data);}while($old_data!==$data);return $data;}function isNumber($data){if(filter_var($data,FILTER_VALIDATE_INT)===0||!filter_var($data,FILTER_VALIDATE_INT)===false){return true;}else{return false;}}function washEmail($data){return filter_var($data,FILTER_SANITIZE_EMAIL);}function washUrl($data){return filter_var($data,FILTER_SANITIZE_URL);}function isEmail($data){if(!filter_var($data,FILTER_VALIDATE_EMAIL)===false){return true;}else{return false;}}function isUrl($data){if(!filter_var($data,FILTER_VALIDATE_URL)===false){return true;}else{return false;}}function washName($data){$data=preg_replace('/[^A-Za-z0-9-]+/','-',$data);$data=preg_replace('/[«»“”!?,.]+/','',$data);return strtolower($data);}function hashed($data){return hash('sha1',md5($data));}} ?>